mapper.select:
SELECT COUNT(*) FROM board bo LEFT JOIN boardcategory c ON bo.cateNo = c.cateNo LEFT JOIN member m ON bo.email = m.email WHERE bo.boDelYn = 'N'
AND bo.cateNo = #{cateNo}
AND ( bo.boTitle LIKE '%' || #{searchWord} || '%' OR bo.boContent LIKE '%' || #{searchWord} || '%' OR m.nickname LIKE '%' || #{searchWord} || '%' )

SELECT * FROM ( SELECT ROWNUM AS rnum, b.* FROM ( SELECT bo.boNo, bo.boTitle, bo.email, m.nickname, bo.boContent, bo.boHit, bo.boRecommend, (SELECT COUNT(*) FROM comments c WHERE c.boNo = bo.boNo AND c.coDelYn = 'N') AS calculatedCommentCount, bo.boRegDate, bo.boDelYn, bo.cateNo, c.cateNm, bo.isNotice FROM board bo LEFT JOIN boardcategory c ON bo.cateNo = c.cateNo LEFT JOIN member m ON bo.email = m.email WHERE bo.boDelYn = 'N'
AND bo.cateNo = #{cateNo}
AND bo.boTitle LIKE '%' || #{searchWord} || '%'
AND bo.boContent LIKE '%' || #{searchWord} || '%'
AND m.nickname LIKE '%' || #{searchWord} || '%'
AND (bo.boTitle LIKE '%' || #{searchWord} || '%' OR bo.boContent LIKE '%' || #{searchWord} || '%' OR m.nickname LIKE '%' || #{searchWord} || '%')
ORDER BY bo.isNotice DESC,
bo.boHit DESC, bo.boRegDate DESC, bo.boNo DESC bo.boRecommend DESC, bo.boRegDate DESC, bo.boNo DESC bo.boRegDate DESC, bo.boNo DESC
) b WHERE ROWNUM <= #{lastRow} ) WHERE rnum >= #{firstRow}

mapper.update:
UPDATE BOARD
SET BODELYN = 'Y'
WHERE BONO = #{boNo}

UPDATE board
SET boHit = NVL(boHit, 0) + 1
WHERE boNo = #{boNo}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS is_recommended
FROM likeboard
WHERE boNo = #{boNo} AND email = #{email}

mapper.update:
UPDATE board
SET boRecommend = (SELECT COUNT(*) FROM likeboard WHERE boNo = #{boNo})
WHERE boNo = #{boNo}
