mapper.select:
		SELECT COUNT(*)
        FROM elecCarInfo
        WHERE 1=1

	          	AND carCateDetail IN

	               #{cate}



	            AND carBrand IN

		                #{brand}



	            AND (carPrice BETWEEN #{selectedMinCarPrice} AND #{selectedMaxCarPrice}
	                 OR carPrice IS NULL)


       		 	AND tbSubYN IN

		        	#{YN}

SELECT rownum, c.*,
FROM () c
LEFT JOIN car_eff_daily ced ON c.carCate = '승용' AND c.memAddr = ced.addr_detail LEFT JOIN truck_eff_daily ted ON c.carCate = '화물' AND c.memAddr = ted.addr_detail LEFT JOIN bus_eff_daily bed ON c.carCate = '승합' AND c.memAddr = bed.addr_detail LEFT JOIN motor_eff_daily med ON c.carCate = '이륜차' AND c.memAddr = med.addr_detail
ORDER BY carPrice ${priceOrder}, singleWarm ${singleOrder}
ORDER BY carPrice ${priceOrder}
ORDER BY singleWarm ${singleOrder}
